import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css';

function AppointmentManagement() {
  const [appointments, setAppointments] = useState([]);
  const [newAppointment, setNewAppointment] = useState({ name: '', date: '', time: '', doctor: '' });

  // Fetch appointments from server (GET request)
  useEffect(() => {
    axios.get('/api/appointments')
      .then(response => setAppointments(response.data))
      .catch(error => console.error('Error fetching appointments:', error));
  }, []);

  // Add a new appointment (POST request)
  const addAppointment = () => {
    axios.post('/api/appointments', newAppointment)
      .then(response => setAppointments([...appointments, response.data]))
      .catch(error => console.error('Error adding appointment:', error));
    setNewAppointment({ name: '', date: '', time: '', doctor: '' });
  };

  // Update an appointment (PUT request)
  const updateAppointment = (index) => {
    const updatedAppointment = { ...appointments[index], time: '1:00 PM' }; // Example: Update time
    axios.put(`/api/appointments/${index}`, updatedAppointment)
      .then(response => {
        const updatedAppointments = [...appointments];
        updatedAppointments[index] = response.data;
        setAppointments(updatedAppointments);
      })
      .catch(error => console.error('Error updating appointment:', error));
  };

  // Delete an appointment (DELETE request)
  const deleteAppointment = (index) => {
    axios.delete(`/api/appointments/${index}`)
      .then(() => {
        const updatedAppointments = appointments.filter((_, i) => i !== index);
        setAppointments(updatedAppointments);
      })
      .catch(error => console.error('Error deleting appointment:', error));
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Appointment Management</h1>
      <div className="form-container">
        <input
          type="text"
          placeholder="Patient Name"
          value={newAppointment.name}
          onChange={(e) => setNewAppointment({ ...newAppointment, name: e.target.value })}
        />
        <input
          type="date"
          placeholder="Date"
          value={newAppointment.date}
          onChange={(e) => setNewAppointment({ ...newAppointment, date: e.target.value })}
        />
        <input
          type="time"
          placeholder="Time"
          value={newAppointment.time}
          onChange={(e) => setNewAppointment({ ...newAppointment, time: e.target.value })}
        />
        <input
          type="text"
          placeholder="Doctor"
          value={newAppointment.doctor}
          onChange={(e) => setNewAppointment({ ...newAppointment, doctor: e.target.value })}
        />
        <button onClick={addAppointment}>Add Appointment</button>
      </div>

      <ul>
        {appointments.length === 0 ? (
          <p>No appointments available</p> // Conditional Rendering: Show this when there are no appointments
        ) : (
          appointments.map((appointment, index) => (
            <li key={index}>
              {appointment.name} - {appointment.date} at {appointment.time} with {appointment.doctor}
              <button onClick={() => updateAppointment(index)}>Update</button> {/* PUT */}
              <button onClick={() => deleteAppointment(index)}>Delete</button> {/* DELETE */}
            </li>
          ))
        )}
      </ul>
    </div>
  );
}

export default AppointmentManagement;
